import React, { useState, useEffect, useContext, useRef } from 'react';
import { Card, CardContent } from './ui/Card';
import { Button } from './ui/Button';
import { CartContext } from '../contexts/CartContext';
import { ShoppingCart } from 'lucide-react';
import { useTranslation } from 'react-i18next';

const ProductList = () => {
  const { t } = useTranslation();
  const { addToCart } = useContext(CartContext);
  const [products, setProducts] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [selectedCollection, setSelectedCollection] = useState('all');

  useEffect(() => {
    const fetchProducts = async () => {
      try {
        const response = await fetch('http://localhost:5000/api/products');
        if (!response.ok) {
          throw new Error(`HTTP error! status: ${response.status}`);
        }
        const data = await response.json();
        setProducts(data);
        setLoading(false);
      } catch (err) {
        setError(err.message);
        setLoading(false);
      }
    };
    fetchProducts();
  }, []);

  if (loading) {
    return (
      <div className="flex justify-center items-center min-h-[400px]">
        <div className="animate-spin rounded-full h-12 w-12 border-b-2 border-blue-500"></div>
      </div>
    );
  }

  if (error) {
    return (
      <div className="text-red-400 text-center p-4 bg-red-900/50 rounded-lg max-w-md mx-auto">
        {t('productList.error', { error })}
      </div>
    );
  }

  const filteredProducts =
    selectedCollection === 'all'
      ? products
      : products.filter(
          (product) => product.collection_id === parseInt(selectedCollection)
        );

  // Fonction d'animation qui clone le bouton et l'anime vers le haut de l'écran
  const animateButtonToTop = (btnElement) => {
    if (!btnElement) return;
    const btnRect = btnElement.getBoundingClientRect();

    const clone = btnElement.cloneNode(true);
    clone.style.position = 'fixed';
    clone.style.top = `${btnRect.top}px`;
    clone.style.left = `${btnRect.left}px`;
    clone.style.width = `${btnRect.width}px`;
    clone.style.height = `${btnRect.height}px`;
    clone.style.zIndex = 9999;
    clone.style.transition = 'all 0.8s ease-in-out';
    clone.style.opacity = '1';

    document.body.appendChild(clone);

    requestAnimationFrame(() => {
      clone.style.top = `0px`;
      clone.style.width = `${btnRect.width * 0.2}px`;
      clone.style.height = `${btnRect.height * 0.2}px`;
      clone.style.opacity = '0';
    });

    clone.addEventListener('transitionend', () => {
      clone.remove();
    });
  };

  const handleAddToCart = (product, e) => {
    addToCart(product);
    animateButtonToTop(e.currentTarget);
  };

  return (
    <div className="min-h-screen bg-gray-900 py-12">
      <div className="max-w-8xl mx-auto px-4 sm:px-6 lg:px-8">
        {/* En-tête */}
        <div className="flex flex-col md:flex-row justify-between items-center mb-8 gap-4">
          <h2 className="text-3xl font-extrabold text-white tracking-wide">
            {t('productList.header.title')}
          </h2>
          <div className="flex gap-2">
            <Button
              variant={selectedCollection === 'all' ? 'default' : 'outline'}
              onClick={() => setSelectedCollection('all')}
            >
              {t('productList.buttons.allProducts')}
            </Button>
            <Button
              variant={selectedCollection === '1' ? 'default' : 'outline'}
              onClick={() => setSelectedCollection('1')}
            >
              {t('productList.buttons.classic')}
            </Button>
            <Button
              variant={selectedCollection === '2' ? 'default' : 'outline'}
              onClick={() => setSelectedCollection('2')}
            >
              {t('productList.buttons.editions')}
            </Button>
          </div>
        </div>

        {/* Affichage des produits */}
        {filteredProducts.length === 0 ? (
          <div className="text-center text-gray-200 py-8">
            {t('productList.noProducts')}
          </div>
        ) : (
          <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 xl:grid-cols-4 gap-6">
            {filteredProducts.map((product) => (
              <Card
                key={product.id}
                className="flex flex-col h-full bg-gray-800/50 border border-gray-700
                           backdrop-blur-sm transition-all duration-300
                           hover:scale-105 hover:-translate-y-2
                           hover:shadow-2xl hover:shadow-yellow-500/40
                           rounded-lg overflow-hidden"
              >
                <div className="aspect-square relative overflow-hidden">
                  <img
                    src={product.image_url || ""}
                    alt={product.nom}
                    className="object-cover w-full h-full transition-transform duration-500 ease-in-out transform hover:scale-110"
                    onError={(e) => {
                      e.target.src = "";
                    }}
                  />
                  {product.stock < 5 && product.stock > 0 && (
                    <div className="absolute top-2 right-2 bg-yellow-500 text-white px-2 py-1 rounded text-sm font-bold">
                      {t('productList.lowStock', { count: product.stock })}
                    </div>
                  )}
                  {product.stock === 0 && (
                    <div className="absolute inset-0 bg-black/70 flex items-center justify-center">
                      <span className="text-white font-bold text-lg">
                        {t('productList.outOfStock')}
                      </span>
                    </div>
                  )}
                </div>
                <CardContent className="flex flex-col flex-grow p-4 text-gray-100">
                  <h3 className="font-bold text-lg mb-2">{product.nom}</h3>
                  <p className="text-gray-300 mb-4 flex-grow">
                    {product.description}
                  </p>
                  <div className="flex items-center justify-between mt-auto">
                    <div className="flex flex-col">
                      <span className="text-lg font-bold text-yellow-400">
                        {Number(product.prix).toFixed(2)} €
                      </span>
                      <span className="text-sm text-gray-400">
                        {product.stock > 0
                          ? t('productList.inStock', { count: product.stock })
                          : t('productList.unavailable')}
                      </span>
                    </div>
                    <Button
                      onClick={(e) => handleAddToCart(product, e)}
                      disabled={product.stock === 0}
                      variant="ghost"
                      className="bg-transparent hover:bg-transparent border-none shadow-none p-0 text-inherit transition-all duration-300 flex items-center justify-center relative group"
                    >
                      <img
                        src="data:image/png;base64,"
                        alt={t('productList.imageAlt', { name: product.nom })}
                        className="object-contain w-[160px] h-auto transition-transform duration-300 group-hover:scale-110"
                      />
                      <div className="absolute bottom-0 right-0 flex items-center justify-center bg-yellow-400 rounded-full p-2 shadow-md transition-transform duration-300 group-hover:scale-110">
                        <ShoppingCart className="w-5 h-5 text-gray-900" />
                      </div>
                    </Button>
                  </div>
                </CardContent>
              </Card>
            ))}
          </div>
        )}
      </div>
    </div>
  );
};

export default ProductList;
